% reddUp% by Martin Rolfs, edited by Alex White (What the hell does reddUp mean?)% Shuts down eyelink and gets edf file % if closeScreen, also closes the psychtoolbox creen function reddUp(closeScreen)global task scr% get eyelink data file on subject computerif task.EYE==1    status = Eyelink('ReceiveFile');     if status == 0        fprintf(1,'\n\nFile transfer went pretty well\n\n');    elseif status < 0        fprintf(1,'\n\nError occurred during file transfer\n\n');    else        fprintf(1,'\n\nFile has been transferred (%i Bytes)\n\n',status)    end            % Eyelink runterfahren    Eyelink('closefile');    Eyelink('shutdown');end% Close the audio device:if task.usePortAudio    PsychPortAudio('Close'); else    Snd('Close');end% Screens schliessenif closeScreen    % re-enable keyboard    ListenChar(1);    ShowCursor;    % Screen(visual.main,'Resolution', scr.oldRes);    Screen('CloseAll');    RestoreCluts; %to undo effect of loading normalized gamma table         if task.doDataPixx        PsychDataPixx('Close');    end        %switch screen back to the original resolution if necessary     if scr.changeRes        SetResolution(0,scr.oldRes);    endend